import React, {useEffect} from 'react';
import { useParams, useNavigate } from 'react-router-dom';

export default function VerifySuccess(){
  const { token } = useParams();
  const navigate = useNavigate();

  useEffect(()=> {
    // Optionally hit backend verify endpoint so server marks user verified.
    fetch((process.env.REACT_APP_SERVER_URL || 'http://localhost:5000') + '/api/auth/verify/' + token)
      .then(()=> {
        setTimeout(()=> navigate('/login'), 2000);
      })
      .catch(()=> setTimeout(()=> navigate('/login'), 2000));
  }, [token]);

  return <div style={{textAlign:'center',marginTop:50}}>
    <h2>Email verified!</h2>
    <p>Redirecting to login...</p>
  </div>;
}
